import React, { useState } from 'react';
import { handleError } from '../utils';

function ExpenseForm({ addTransaction }) {
    const [expenseInfo, setExpenseInfo] = useState({
        amount: '',
        text: '',
        type: 'expense' // default type
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setExpenseInfo(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const { amount, text, type } = expenseInfo;

        if (!amount || !text || !type) {
            handleError('Please fill in all fields');
            return;
        }

        addTransaction(expenseInfo);
        setExpenseInfo({ amount: '', text: '', type: 'expense' });
    };

    return (
        <div className='flex items-center justify-center'>
            <div className="pb-6 w-full">
                <h1 className='text-2xl lg:text-4xl font-semibold text-center p-4 w-auto shadow-lg text-gray-300'>
                    Expense Manager
                </h1>
                <div className='flex items-start justify-center text-center'>
                    <form onSubmit={handleSubmit} className='pt-5 w-1/3'>
                        {/* Text */}
                        <div className='text-start mb-2'>
                            <label htmlFor='text' className='font-medium text-xl block text-gray-300'>
                                Detail
                            </label>
                            <input
                                type='text'
                                name='text'
                                value={expenseInfo.text}
                                onChange={handleChange}
                                placeholder='Enter description...'
                                className='bg-gray-700 text-white p-3 rounded-lg w-full focus:ring-2 focus:ring-purple-500 focus:outline-none'
                            />
                        </div>

                        {/* Amount */}
                        <div className='text-start mb-2'>
                            <label htmlFor='amount' className='font-medium text-xl block text-gray-300'>
                                Amount
                            </label>
                            <input
                                type='number'
                                name='amount'
                                value={expenseInfo.amount}
                                onChange={handleChange}
                                placeholder='Enter amount...'
                                className='bg-gray-700 text-white p-3 rounded-lg w-full focus:ring-2 focus:ring-purple-500 focus:outline-none'
                            />
                        </div>

                        {/* Type */}
                        <div className='text-start mb-4'>
                            <label htmlFor='type' className='font-medium text-xl block text-gray-300'>
                                Type
                            </label>
                            <select
                                name='type'
                                value={expenseInfo.type}
                                onChange={handleChange}
                                className='bg-gray-700 text-white p-3 rounded-lg w-full focus:ring-2 focus:ring-purple-500 focus:outline-none'
                            >
                                <option value='expense'>Expense</option>
                                <option value='income'>Income</option>
                            </select>
                        </div>

                        <button
                            type='submit'
                            className='w-full mt-4 bg-purple-600 rounded-lg px-2 py-2 text-white font-semibold hover:bg-purple-800 hover:shadow-lg transition duration-300'
                        >
                            Add Transaction
                        </button>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default ExpenseForm;
