    return (
        <div className="expense-list relative bg-yellow-200 w-1/2 transform translate-x-1/2 pt-6">
            {expenses.map((expense, index) => (
                <div key={index} className="expense-item flex justify-between items-center  ">
                    <button className="delete-button bg-red-500 text-white border-none px-3 py-1 text-sm cursor-pointer rounded font-" onClick={() =>
                        deleteExpens(expense._id)}>X</button>
                    <div className="expense-description font-semibold">{expense.text}</div>
                    <div
                        className="expense-amount font-semibold "
                        style={{ color: expense.amount > 0 ? '#27ae60' : '#c0392b' }}
                    >â‚¹{expense.amount}</div>
                </div>
            ))}
        </div>
    );
};

