import React, { useState } from 'react'
import { handleError } from '../utils';

function ExpenseForm({ addTransaction }) {

    const [expenseInfo, setExpenseInfo] = useState({
        amount: '',
        text: ''
    })

    const handleChange = (e) => {
        const { name, value } = e.target;
        const copyExpenseInfo = { ...expenseInfo };
        copyExpenseInfo[name] = value;
        setExpenseInfo(copyExpenseInfo);
    }

    const addExpenses = (e) => {
        e.preventDefault();
        const { amount, text } = expenseInfo;
        if (!amount || !text) {
            handleError('Please add Expense Details');
            return;
        }
        addTransaction(expenseInfo);
        setExpenseInfo({ amount: '', text: '' })
    }

    return (
        <div className='container'>
            <h1 className='text-2xl lg:text-3xl font-semibold flex items-center justify-center pt-4 w-screen' >Expense Tracker</h1>
            <div className='flex items-start justify-center w-screen '>
            <form onSubmit={addExpenses} className=' bg-green-500 pt-5 w-1/3'>
                <div className='text-start mb-2'>
                    <label 
                        htmlFor='text' 
                        className='font-medium text-xl block '>Expense Detail
                    </label>
                    <input
                        onChange={handleChange}
                        type='text'
                        name='text'
                        placeholder='Enter your Expense Detail...'
                        value={expenseInfo.text}
                        className=' border border-gray-300 text-gray-900 text-sm rounded-lg p-2 w-full'
                    />
                </div>
                <div className=' text-start mb-2'>
                    <label
                     htmlFor='amount ' 
                     className=' font-medium block text-xl'>
                        Amount 
                     </label>
                    <input
                        onChange={handleChange}
                        type='number'
                        name='amount'
                        placeholder='Enter your Amount...'
                        value={expenseInfo.amount}
                        className='border border-gray-300 text-gray-900 text-sm rounded-lg p-2 w-full'
                    />
                </div>
                <button type='submit w-full '>Add Expense</button>
            </form>
            </div>
        </div>
    )
}

export default ExpenseForm
